import React from "react";
import { useState } from "react";
import {
  Button,
  Card,
  Col,
  Row,
  Container,
  Modal,
  Image,
} from "react-bootstrap";

function Modals({ projects }) {
  const handleClose = () => setShowModal(false);

  return (
    <Modal
      show={showModal}
      onHide={handleClose}
      backdrop="static"
      keyboard={true}
      style={{ backgroundColor: "#0D1B2A" }}
    >
      <Modal.Header closeButton>
        <Modal.Title>{projects.name}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Image
          src={projects.cardBg}
          style={{ maxWidth: "450px", objectFit: "contain" }}
        ></Image>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default Modals;
